package org.example.Ex2;/*Есть абстрактный класс ПочтовоеОтправление, в котором представлен набор характеристик:наименование;адрес отправителя;адрес получателя;и абстрактное поведение:доставить;отправить;Реализуйте методы этого абстрактного класса в его потомкахПисьмо, Бандероль, Посылка, Контейнер с учетом той специфики, которая характерна для каждого типа почтовыхотправлений на Ваше усмотрение.*/public abstract class Mailing {    private String name;    private String sendersAddress;    private String addressRecipient;    public Mailing(String name, String sendersAddress, String addressRecipient) {        this.name = name;        this.sendersAddress = sendersAddress;        this.addressRecipient = addressRecipient;    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public String getSendersAddress() {        return sendersAddress;    }    public void setSendersAddress(String sendersAddress) {        this.sendersAddress = sendersAddress;    }    public String getAddressRecipient() {        return addressRecipient;    }    public void setAddressRecipient(String addressRecipient) {        this.addressRecipient = addressRecipient;    }    public abstract void deliver();    public abstract void send();}